import React, { useEffect, useState } from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import Cancel from "./cancel";
import "./App.css";
import Success from "./success";
import { Elements } from "@stripe/react-stripe-js";
import { loadStripe } from "@stripe/stripe-js";
import CheckoutForm from "./checkout";

// Use your publishable key here
const stripePromise = loadStripe(
  "pk_test_51NaRZrFqCKOUMOaDzScghtYTMexZnjlIkLVDe6TjTUUOjOBdDXZVE46eYAc6AcdwHh6B5Lxba3iyQfbIs0XdYOpx00y01Zq5Qj"
);

const ProductDisplay = () => {
  const [isPayment, setIsPayment] = useState(false);
  const [clientSecret, setClientSecret] = useState("");

  useEffect(() => {
    fetch("/create-payment-intent", {
      method: "POST",
    })
      .then((res) => res.json())
      .then((data) => {
        setClientSecret(data.client_secret);
      })
      .catch((error) => console.error("Error:", error));
  }, []);

  const handleSubmit = (event) => {
    event.preventDefault();
    setIsPayment(true);
  };

  return (
    <>
      <h1
        style={{
          color: "white",
        }}
      >
        Payment
      </h1>
      {isPayment ? (
        clientSecret && (
          <section>
          <div className='product row'>
            <img
              className='col-6 img-fluid'
              src='https://i.imgur.com/EHyR2nP.png'
              alt='The cover of Stubborn Attachments'
            />
            <div className='description col-6 d-flex flex-column justify-content-center align-items-center'>
              <h3>Stubborn Attachments</h3>
              <h5>$20.00</h5>
              <form onSubmit={handleSubmit}>
                <button type='submit'>Checkout</button>
              </form>
            </div>
          </div>
        </section>

        )
      ) : (
        <Main handleSubmit={handleSubmit} />
      )}
    </>
  );
};

const Main = ({ handleSubmit }) => (
  <section>
    <div className='product row'>
      <img
        className='col-6 img-fluid'
        src='https://i.imgur.com/EHyR2nP.png'
        alt='The cover of Stubborn Attachments'
      />
      <div className='description col-6 d-flex flex-column justify-content-center align-items-center'>
        <h3>Stubborn Attachments</h3>
        <h5>$20.00</h5>
        <form onSubmit={handleSubmit}>
          <button type='submit'>Checkout</button>
        </form>
      </div>
    </div>
  </section>
);

export default function App() {
  return (
    <Router>
      <Routes>
        <Route path='/checkout' element={<ProductDisplay />} />
        <Route path='/cancel' element={<Cancel />} />
        <Route path='/success' element={<Success />} />
      </Routes>
    </Router>
  );
}
