import React, { useEffect, useState } from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import Cancel from "./cancel";
import "./App.css";
// import Success from "./success";
import { Elements } from "@stripe/react-stripe-js";
import { loadStripe } from "@stripe/stripe-js";
import CheckoutForm from "./checkout";
import { useNavigate } from "react-router-dom";
import BuyButton from "./button";

// Use your publishable key here
const stripePromise = loadStripe(
  "pk_test_51NaRZrFqCKOUMOaDzScghtYTMexZnjlIkLVDe6TjTUUOjOBdDXZVE46eYAc6AcdwHh6B5Lxba3iyQfbIs0XdYOpx00y01Zq5Qj"
);

const ProductDisplay = () => {
  const [isPayment, setIsPayment] = useState(false);
  const [clientSecret, setClientSecret] = useState("");

  useEffect(() => {
    fetch("/create-payment-intent", {
      method: "POST",
    })
      .then((res) => res.json())
      .then((data) => {
        setClientSecret(data.client_secret);
      })
      .catch((error) => console.error("Error:", error));
  }, []);

  const handleSubmit = (event) => {
    event.preventDefault();
    setIsPayment(true);
  };
  const appearance = {
    theme: "stripe",

    variables: {
      colorPrimary: "#556CD6",
      colorBackground: "gray",
      colorText: "white",
      colorDanger: "red",
      fontFamily: "Ideal Sans, system-ui, sans-serif",
      spacingUnit: "4px",
      borderRadius: "8px",
      // See all possible variables below
    },
    rules: {
      ".Tab": {
        border: "1px solid #E0E6EB",
        boxShadow:
          "0px 1px 1px rgba(0, 0, 0, 0.03), 0px 3px 6px rgba(18, 42, 66, 0.02)",
      },

      ".Tab:hover": {
        color: "var(--colorText)",
      },

      ".Tab--selected": {
        borderColor: "#E0E6EB",
        boxShadow:
          "0px 1px 1px rgba(0, 0, 0, 0.03), 0px 3px 6px rgba(18, 42, 66, 0.02), 0 0 0 2px var(--colorPrimary)",
      },

      ".Input--invalid": {
        boxShadow:
          "0 1px 1px 0 rgba(0, 0, 0, 0.07), 0 0 0 2px var(--colorDanger)",
      },

      // See all supported class names and selector syntax below
    },
  };

  return (
    <>
      {isPayment ? (
        clientSecret && (
          <section className=' overflow-auto'>
            <div className='product row justify-content-between'>
              <img
                className='col-12 col-md-4 img-fluid'
                src='https://i.imgur.com/EHyR2nP.png'
                alt='The cover of Stubborn Attachments'
              />
              <div className='description col-12 col-md-6 d-flex flex-column justify-content-center align-items-center mt-4'>
                <h1>Payment</h1>
                <h3>Stubborn Attachments</h3>
                <h5>$41.00</h5>
                <Elements
                  stripe={stripePromise}
                  options={{ clientSecret, appearance }}
                >
                  <CheckoutForm />
                </Elements>
              </div>
            </div>
          </section>
        )
      ) : (
        <>
          <Main handleSubmit={handleSubmit} />
        </>
      )}
    </>
  );
};

const Main = ({ handleSubmit }) => (
  <section>
    <div className='product row'>
      <img
        className='col-12 col-md- img-fluid'
        src='https://i.imgur.com/EHyR2nP.png'
        alt='The cover of Stubborn Attachments'
      />
      <div className='description col-12 col-md-6 d-flex flex-column justify-content-center align-items-center mt-4'>
        <h1 className='position-absolute top-0 start-50 '>Payment</h1>
        <h3>Stubborn Attachments</h3>
        <h5>$20.00</h5>
        <form onSubmit={handleSubmit}>
          <button type='submit'>Buy Product</button>
        </form>
      </div>
    </div>
  </section>
);

const Success = () => {
  const navigate = useNavigate();

  const MoveToHomes = () => {
    navigate("/checkout");
  };
  return (
    <section>
      <div className='product row'>
        <img
          className='col-12 col-md-6 img-fluid'
          src='https://i.imgur.com/EHyR2nP.png'
          alt='The cover of Stubborn Attachments'
        />
        <div className='description col-12 col-md-6 d-flex flex-column justify-content-center align-items-center mt-4'>
          <h1>Payment</h1>
          <h1>Success</h1>
          <h2 className='mt-3'>Thank you for your purchase!</h2>
          <BuyButton label='Return to Home' onClick={() => MoveToHomes()} />
        </div>
      </div>
    </section>
  );
};

export default function App() {
  return (
    <Router>
      <Routes>
        <Route path='/checkout' element={<ProductDisplay />} />
        <Route path='/cancel' element={<Cancel />} />
        <Route path='/success' element={<Success />} />
      </Routes>
    </Router>
  );
}
