import React, { useEffect, useState } from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import Cancel from "./cancel";
import "./App.css";
// import Success from "./success";
import { Elements } from "@stripe/react-stripe-js";
import { loadStripe } from "@stripe/stripe-js";
import CheckoutForm from "./checkout";
import { useNavigate } from "react-router-dom";
import BuyButton from "./button";

// Use your publishable key here
const stripePromise = loadStripe(
  "pk_test_51NaRZrFqCKOUMOaDzScghtYTMexZnjlIkLVDe6TjTUUOjOBdDXZVE46eYAc6AcdwHh6B5Lxba3iyQfbIs0XdYOpx00y01Zq5Qj"
);

const ProductDisplay = () => {
  const [isLoading, setIsLoading] = useState(false);
  const [isPayment, setIsPayment] = useState(false);
  const [Productdetails, setProductDetails] = useState({});
  console.log(Productdetails, "ProductdetailsProductdetailsProductdetails");
  const [clientSecret, setClientSecret] = useState("");

  useEffect(() => {
    setIsLoading(true);
    fetch("/create-payment-intent", {
      method: "POST",
    })
      .then((res) => res.json())
      .then((data) => {
        setClientSecret(data.client_secret);
        setProductDetails((prev) => {
          return {
            ...prev,
            product_name: data.product_name,
            product_description: data.product_description,
            product_price: data.product_price,
            product_image: data.product_image,
          };
        });
      })
      .catch((error) => {
        setIsLoading(false);
        console.error("Error:", error);
      });
  }, []);

  const handleSubmit = (event) => {
    event.preventDefault();
    setIsPayment(true);
  };
  const appearance = {
    theme: "night",
    variables: {
      fontFamily: "Sohne, system-ui, sans-serif",
      fontWeightNormal: "500",
      borderRadius: "8px",
      colorBackground: "#0A2540",
      colorPrimary: "#EFC078",
      accessibleColorOnColorPrimary: "#1A1B25",
      colorText: "white",
      colorTextSecondary: "white",
      colorTextPlaceholder: "#ABB2BF",
      tabIconColor: "white",
      logoColor: "dark",
    },
    rules: {
      ".Tab": {
        padding: "5px 5px",
        margin: "15px 5px",
      },
      ".Input": {
        backgroundColor: "#212D63",
        border: "1px solid var(--colorPrimary)",
      },
    },
  };
  return (
    <>
      {isPayment ? (
        clientSecret && (
          <section>
            <div className='product row justify-content-between'>
              <img
                className='col-12 col-md-6 img-fluid'
                src='https://i.imgur.com/EHyR2nP.png'
                alt='The cover of Stubborn Attachments'
              />
              <div className='description col-12 col-md-6 d-flex flex-column justify-content-center align-items-center mt-4'>
                <h1>Payment</h1>
                <h3>{Productdetails.product_name}</h3>
                <h5>${Productdetails.product_price}</h5>
                <Elements
                  stripe={stripePromise}
                  options={{ clientSecret, appearance }}
                >
                  <CheckoutForm />
                </Elements>
              </div>
            </div>
          </section>
        )
      ) : (
        <>
          <Main
            handleSubmit={handleSubmit}
            ProductDisplay={Productdetails}
            isLoading={isLoading}
          />
        </>
      )}
    </>
  );
};

const Main = ({ handleSubmit, ProductDisplay, isLoading }) => {
  console.log(isLoading, "isLoading");
  return (
    <>
      {isLoading && (
        <section>
          <div className='product row justify-content-between'>
            <img
              className='col-12 col-md-6 img-fluid'
              src={ProductDisplay.product_image}
              alt='The cover of Stubborn Attachments'
            />
            <div className='description col-12 col-md-6 d-flex flex-column justify-content-center align-items-center mt-4'>
              <h1>Payment</h1>
              <h3>{ProductDisplay.product_name}</h3>
              <h5>${ProductDisplay.product_price}</h5>
              <form onSubmit={handleSubmit}>
                <button type='submit'>Buy Product</button>
              </form>
            </div>
          </div>
        </section>
      )}
    </>
  );
};

const Success = () => {
  const navigate = useNavigate();

  const MoveToHomes = () => {
    navigate("/checkout");
  };
  return (
    <section>
      <div className='product row'>
        <img
          className='col-12 col-md-6 img-fluid'
          src='https://i.imgur.com/EHyR2nP.png'
          alt='The cover of Stubborn Attachments'
        />
        <div className='description col-12 col-md-6 d-flex flex-column justify-content-center align-items-center mt-4'>
          <h1>Payment</h1>
          <h1>Success</h1>
          <h2 className='mt-3'>Thank you for your purchase!</h2>
          <BuyButton label='Return to Home' onClick={() => MoveToHomes()} />
        </div>
      </div>
    </section>
  );
};

export default function App() {
  return (
    <Router>
      <Routes>
        <Route path='/checkout' element={<ProductDisplay />} />
        <Route path='/cancel' element={<Cancel />} />
        <Route path='/success' element={<Success />} />
      </Routes>
    </Router>
  );
}
